export class SettingCard extends HTMLElement{constructor(){super(),this.attachShadow({mode:"open"});const e=this.getTemplate(),t=this.innerHTML;this.innerHTML="",this.shadowRoot.appendChild(e.content.cloneNode(!0)),this.shadowRoot.querySelector(".setting_card .content").innerHTML=t;try{this.shadowRoot.querySelector("button.update_settings").onclick=e=>{this.updateSettings(e.target)}}catch(e){console.error("Save settings button does not exist")}}getTemplate(){const e=this.getAttribute("card-title"),t=document.createElement("template");return t.innerHTML=`\n        <link rel="stylesheet" href="./dist/card-styles.css">\n\n        <div class="setting_card">\n            <h1 class="card_title">${e}</h1>\n            <div class="content"></div>\n        </div>`,t}updateSettings(e){e.classList.add("processing"),e.closest(".content").classList.add("processing"),console.log("processing")}}window.customElements.define("setting-card",SettingCard);export class FormInput extends HTMLElement{constructor(){super(),this.attachShadow({mode:"open"});const e=this.getTemplate();this.innerHTML="",this.shadowRoot.appendChild(e.content.cloneNode(!0))}getTemplate(){const e=document.createElement("template"),t=this.getInput();return e.innerHTML=`\n        <link rel="stylesheet" href="./dist/input-styles.css">\n        ${t}`,e}getInput(){const e=this.gerDefaults();return"toggle"===e.type?`\n            <p>\n                <label for="${e.name}">${e.title}:</label>\n                <label class="toggle">\n                    <input\n                        type="checkbox"\n                        `+(e.checked?'checked="checked"':"")+`\n                        name="${e.name}"\n                        value="${e.value}"\n                    >\n                    <span class="slider"></span>\n                </label>\n            </p>`:`\n            <p>\n                <label for="${e.name}">${e.title}:</label>\n                <input\n                    type="${e.type}"\n                    name="${e.name}"\n                    id="${e.name}"\n                    value="${e.value}"\n                    `+(e.required?'required="required"':"")+`\n                    placeholder="${e.placeholder}"\n                >\n                <small>${e.helper_text}</small>\n            </p>`}gerDefaults(){return{type:this.getAttribute("type")||"text",title:this.getAttribute("title")||"",name:this.getAttribute("name")||"",required:this.getAttribute("required")||!1,value:this.getAttribute("value")||"",placeholder:this.getAttribute("placeholder")||"",checked:this.getAttribute("checked")||!1,helper_text:this.textContent||""}}}window.customElements.define("form-input",FormInput);
